mapper.select:
select person.id id,type1.library_name faceTypeCode,person.alarm_level alarmLevel,person.control_reason controlReason, person.person_name personName,person.phone phone,person.person_paper_num personPaperNum,person.image_path personImg,user1.cname createUserName, date_format(control_beginTime,'%Y-%m-%d %H:%i:%s') beginTime,date_format(control_endTime,'%Y-%m-%d %H:%i:%s') endTime from face_type_person person inner join face_type type1 on type1.face_type_code=person.face_type_code left join ht_user user1 on user1.user_id=person.create_by where control_status=0
and person.face_type_code=#{faceTypeCode}
and person.alarm_level=#{alarmLevel}
and concat_WS(" ",person.face_type_code,person.alarm_level,person.control_reason,person.person_name,person.phone,person.person_paper_num,person.create_by) LIKE concat("%",#{likeStr},"%")
order by person.update_date desc

select count(*) from ( select person.id id from face_type_person person inner join face_type type1 on type1.face_type_code=person.face_type_code left join ht_user user1 on user1.user_id=person.create_by where control_status=0
and person.face_type_code=#{faceTypeCode}
and person.alarm_level=#{alarmLevel}
and concat_WS(" ",person.face_type_code,person.alarm_level,person.control_reason,person.person_name,person.phone,person.person_paper_num,person.create_by) LIKE concat("%",#{likeStr},"%")
)as total
